(function($){"use strict";$(window).on('elementor/frontend/init',()=>{elementorFrontend.hooks.addAction('frontend/element_ready/technocy-brand.default',($scope)=>{var $carousel=$('.technocy-carousel',$scope);if($carousel.length>0){var data=$carousel.data('settings'),rtl=$('body').hasClass('rtl')?!0:!1;$carousel.slick({rtl:rtl,dots:data.navigation=='both'||data.navigation=='dots'?!0:!1,arrows:data.navigation=='both'||data.navigation=='arrows'?!0:!1,infinite:data.loop,speed:300,slidesToShow:parseInt(data.items),autoplay:data.autoplay,autoplaySpeed:data.autoplaySpeed,slidesToScroll:1,lazyLoad:'ondemand',responsive:[{breakpoint:parseInt(data.breakpoint_laptop),settings:{slidesToShow:parseInt(data.items_laptop),}},{breakpoint:parseInt(data.breakpoint_tablet_extra),settings:{slidesToShow:parseInt(data.items_tablet_extra),}},{breakpoint:parseInt(data.breakpoint_tablet),settings:{slidesToShow:parseInt(data.items_tablet),}},{breakpoint:parseInt(data.breakpoint_mobile_extra),settings:{slidesToShow:parseInt(data.items_mobile_extra),}},{breakpoint:parseInt(data.breakpoint_mobile),settings:{slidesToShow:parseInt(data.items_mobile),}}]})}})})})(jQuery)