(function($){"use strict";$(window).on('elementor/frontend/init',()=>{elementorFrontend.hooks.addAction('frontend/element_ready/technocy-products.default',($scope)=>{let $carousel=$('.woocommerce-carousel',$scope);if($carousel.length>0){let data=$carousel.data('settings'),rtl=$('body').hasClass('rtl')?!0:!1;$('ul.products',$carousel).slick({rtl:rtl,dots:data.navigation=='both'||data.navigation=='dots'?!0:!1,arrows:data.navigation=='both'||data.navigation=='arrows'?!0:!1,infinite:data.loop,speed:300,slidesToShow:parseInt(data.items),autoplay:data.autoplay,autoplaySpeed:parseInt(data.autoplayTimeout),slidesToScroll:1,lazyLoad:'ondemand',responsive:[{breakpoint:parseInt(data.breakpoint_laptop),settings:{slidesToShow:parseInt(data.items_laptop),}},{breakpoint:parseInt(data.breakpoint_tablet_extra),settings:{slidesToShow:parseInt(data.items_tablet_extra),}},{breakpoint:parseInt(data.breakpoint_tablet),settings:{slidesToShow:parseInt(data.items_tablet),}},{breakpoint:parseInt(data.breakpoint_mobile_extra),settings:{slidesToShow:parseInt(data.items_mobile_extra),}},{breakpoint:parseInt(data.breakpoint_mobile),settings:{slidesToShow:parseInt(data.items_mobile),}}]})}
$('.gallery_item').on('click',function(e){let $this=$(this),$parent=$this.closest('.product-block'),$image=$parent.find('.product-image > img'),image=$this.data('image'),scrset=$this.data('scrset');$this.addClass('active');$this.siblings('.active').removeClass('active');$image.attr('src',image);$image.attr('srcset',scrset)});let heightHideInfo=$scope.find('.product-block .product-caption-bottom').outerHeight();$scope.find('.product-block .content-product-imagin').css({marginBottom:-heightHideInfo})})})})(jQuery)